import React, { useState, useEffect } from "react";
import "./Projects.css";

function Projects() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState("");
  const [action, setAction] = useState("");

  useEffect(() => {
    // Pobieranie użytkowników z bazy danych
    fetch("URL_DO_BAZY_DANYCH")
      .then((response) => response.json())
      .then((data) => {
        setUsers(data); // Ustawianie pobranych użytkowników w stanie komponentu
      })
      .catch((error) => {
        console.error("Błąd podczas pobierania użytkowników:", error);
      });
  }, []);

  const handleAddUser = () => {
    // Wykonaj akcję dodania użytkownika w projekcie (dodaj logikę)
    console.log("Dodaj użytkownika: ", selectedUser);
  };

  const handleRemoveUser = () => {
    // Wykonaj akcję usunięcia użytkownika z projektu (dodaj logikę)
    console.log("Usuń użytkownika: ", selectedUser);
  };

  return (
    <div>
      <h2>Lista projektów:</h2>
      <div className="project-grid">
        {users.map((user) => (
          <div key={user.id} className="project-card">
            <h3>{user.name}</h3>
            <p>{user.description}</p>
          </div>
        ))}
      </div>
      <div className="user-actions">
        <select
          onChange={(e) => setSelectedUser(e.target.value)}
          value={selectedUser}
        >
          <option value="">Wybierz użytkownika</option>
          {users.map((user) => (
            <option key={user.id} value={user.name}>
              {user.name}
            </option>
          ))}
        </select>
        <button onClick={handleAddUser}>Dodaj użytkownika</button>
        <button onClick={handleRemoveUser}>Usuń użytkownika</button>
      </div>
    </div>
  );
}

export default Projects;

