import React, { useState, useEffect } from "react";

function Projects() {
  const [projects, setProjects] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Wysyłamy żądanie do backendu, aby pobrać projekty
    fetch("https://pcz-app-default-rtdb.firebaseio.com/projects") // Zmień URL na odpowiedni
      .then((response) => {
        if (!response.ok) {
          throw new Error("Błąd sieciowy lub błąd serwera");
        }
        return response.json();
      })
      .then((data) => {
        setProjects(data.projects); // Zakładamy, że odpowiedź zawiera pole "projects" z tablicą projektów
        setIsLoading(false);
      })
      .catch((error) => {
        console.error("Błąd podczas pobierania projektów:", error);
        setIsLoading(false);
      });
  }, []);

  if (isLoading) {
    return <p>Ładowanie projektów...</p>;
  }

  return (
    <div>
      <h2>Lista projektów:</h2>
      <div className="project-grid">
        {projects.map((project) => (
          <div key={project.id} className="project-card">
            <h3>{project.name}</h3>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Projects;
