import React, { useState } from "react";
import "./Columns.css";
import AddItem from "../AddItem/AddItem";

function Columns() {
  const [editItem, setEditItem] = useState(null);
  const [editedContent1, setEditedContent1] = useState("");
  const [editedContent2, setEditedContent2] = useState("");
  const [editedContent3, setEditedContent3] = useState("");
  const [editingColumnIndex, setEditingColumnIndex] = useState(null);
  const [editingItem, setEditingItem] = useState(null);
  const [isSaving, setIsSaving] = useState(false);

  const [columns, setColumns] = useState([
    { title: "To do", items: [] },
    { title: "In progress", items: [] },
    { title: "Review", items: [] },
    { title: "Done", items: [] },
  ]);

  const handleAddItem = (content, columnIndex) => {
    // Dodaj nowy element do kolumny
    setColumns((prevColumns) => {
      const updatedColumns = [...prevColumns];
      updatedColumns[columnIndex].items.push(content);
      return updatedColumns;
    });
  };

  const saveChanges = () => {
    // Zapisz zmiany do bazy danych
    setIsSaving(true);
    const dataToSave = { columns: columns };
    fetch("https://pcz2-f1ee9-default-rtdb.firebaseio.com/projects.json", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(dataToSave),
    })
      .then((response) => {
        if (response.ok) {
          alert("Zapisano dane do bazy danych.");
        } else {
          alert("Wystąpił błąd podczas zapisywania danych.");
        }
      })
      .catch((error) => {
        console.error("Błąd podczas zapisywania danych:", error);
      })
      .finally(() => {
        setIsSaving(false);
      });
  };

  // Reszta kodu...

  return (
    <div>
      <button onClick={saveChanges} disabled={isSaving}>
        {isSaving ? "Zapisywanie..." : "Save"}
      </button>
      {/* Reszta kodu... */}
    </div>
  );
}

export default Columns;
