import React, { useContext } from "react";
import { Link, useHistory } from "react-router-dom"; // Import Link i useHistory
import AuthContext from "../../store/auth-context";
import classes from "./Navigation.module.css";

const Navigation = () => {
  const ctx = useContext(AuthContext);
  const history = useHistory(); // Użyj useHistory do manipulowania historią przeglądarki

  const handleLogout = () => {
    ctx.onLogout();
    history.push("/"); // Przeniesienie użytkownika na stronę główną po wylogowaniu
  };

  return (
    <nav className={classes.nav}>
      <ul>
        {ctx.isLoggedIn && (
          <li>
            <Link to="/projects">Projects</Link> {/* Użyj Link zamiast a */}
          </li>
        )}

        {ctx.isLoggedIn && (
          <li>
            <button onClick={handleLogout}>Logout</button>
          </li>
        )}
        {!ctx.isLoggedIn && !ctx.isSignIn ? (
          <li>
            <button onClick={ctx.onSignIn}>Sign in</button>
          </li>
        ) : (
          <li>
            <button onClick={ctx.onSignOff}>Login</button>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navigation;
