import React, { useState, useRef } from "react";
import "./Columns.css";
import AddItem from "../AddItem/AddItem";

function Columns() {
  const currentValue = useRef();

  const [columns, setColumns] = useState([
    { title: "To do", items: [] },
    { title: "In progress", items: [] },
    { title: "Review", items: [] },
    { title: "Done", items: [] },
  ]);

  const handleAddItem = (content, columnIndex) => {
    setColumns((prevColumns) => {
      const updatedColumns = [...prevColumns];
      updatedColumns[columnIndex].items.push(content);
      return updatedColumns;
    });
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleOnDrop = (value, columnIndex) => {
    console.log(value + "  " + columnIndex);
    handleAddItem(value, columnIndex);
  };

  return (
    <div className="columns-container">
      {columns.map((column, columnIndex) => (
        <div
          key={columnIndex}
          className="column"
          onDrop={handleOnDrop(currentValue.current, columnIndex)}
          onDragOver={handleDragOver}
        >
          <h2>{column.title}</h2>

          {column.items.map((item, index) => (
            <div
              key={index}
              className="newTile"
              draggable
              onDragEnd={(e) => (currentValue.current = item)}
            >
              {item}
            </div>
          ))}

          <AddItem
            onAddItem={(content) => handleAddItem(content, columnIndex)}
          />
        </div>
      ))}
    </div>
  );
}

export default Columns;
