import React, { useState } from "react";
import "./Columns.css";
import AddItem from "../AddItem/AddItem";

function Columns() {
  const [columns, setColumns] = useState([
    { title: "To do", items: [] },
    { title: "In progress", items: [] },
    { title: "Review", items: [] },
    { title: "Done", items: [] },
  ]);

  const handleAddItem = (content, columnIndex) => {
    setColumns((prevColumns) => {
      const updatedColumns = [...prevColumns];
      updatedColumns[columnIndex].items.push(content);
      return updatedColumns;
    });
  };

  const handleRemoveItem = (e, item, columnIndex) => {
    const id = e.dataTransfer.getData("text/plain-index");
    if (id !== columnIndex) {
      setColumns((prevColumns) => {
        const updatedColumns = [...prevColumns];
        const updatedItems = updatedColumns[columnIndex].items.filter(
          (i) => i !== item
        );
        updatedColumns[columnIndex].items = updatedItems;
        return updatedColumns;
      });
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleOnDrop = (e, columnIndex) => {
    const value = e.dataTransfer.getData("text/plain-item");
    const id = e.dataTransfer.getData("text/plain-index");
    console.log(value + "  " + columnIndex);
    if (id !== columnIndex) {
      handleAddItem(value, columnIndex);
    }
  };

  return (
    <div className="columns-container">
      {columns.map((column, columnIndex) => (
        <div
          key={columnIndex}
          className="column"
          onDrop={(e) => handleOnDrop(e, columnIndex)}
          onDragOver={handleDragOver}
        >
          <h2>{column.title}</h2>

          {column.items.map((item, index) => (
            <div
              key={index}
              className="newTile"
              draggable
              onDragStart={(e) => {
                e.dataTransfer.setData("text/plain-item", item);
                e.dataTransfer.setData("text/plain-index", index);
              }}
              onDragEnd={(e) => {
                //e.dataTransfer.setData("text/plain-index", index);
                //handleRemoveItem(item, index);
              }}
            >
              {item}
            </div>
          ))}

          <AddItem
            onAddItem={(content) => handleAddItem(content, columnIndex)}
          />
        </div>
      ))}
    </div>
  );
}

export default Columns;
