import React, { useContext } from "react";
import { useHistory } from "react-router-dom"; // Import useHistory
import AuthContext from "../../store/auth-context";
import classes from "./Navigation.module.css";

const Navigation = () => {
  const ctx = useContext(AuthContext);
  const history = useHistory(); // Użyj useHistory

  const handleLogout = () => {
    ctx.onLogout();
    history.push("/login"); // Przekieruj na "/login" po wylogowaniu
  };

  const handleSignIn = (href) => {
    ctx.onSignIn();
    history.push(href); // Przekieruj na ścieżkę zdefiniowaną w href
  };

  const handleSignOff = (href) => {
    ctx.onSignOff();
    history.push(href); // Przekieruj na ścieżkę zdefiniowaną w href
  };

  return (
    <nav className={classes.nav}>
      <ul>
        {ctx.isLoggedIn && (
          <li>
            <a href="/projects">Projects</a>
          </li>
        )}

        {ctx.isLoggedIn && (
          <li>
            <button onClick={handleLogout}>Logout</button>
          </li>
        )}
        {!ctx.isLoggedIn && !ctx.isSignIn && (
          <li>
            <button onClick={() => handleSignIn("/sign")}>Sign in</button>
          </li>
        )}
        {!ctx.isLoggedIn && ctx.isSignIn && (
          <li>
            <button onClick={() => handleSignOff("/login")}>Login</button>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navigation;
