import React, { useState } from "react";
import Columns from "./Columns/Columns";

const Home = (props) => {
  const [isSaving, setIsSaving] = useState(false);
  const [columns, setColumns] = useState(props.initialColumns);

  const handleSave = async () => {
    setIsSaving(true);

    // Przygotuj dane do wysłania do bazy danych
    const dataToSave = {
      columns: columns, // Zaktualizowana lista kolumn
    };

    try {
      const response = await fetch(
        "https://pcz2-f1ee9-default-rtdb.firebaseio.com/projects.json",
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(dataToSave),
        }
      );

      if (response.ok) {
        alert("Zapisano dane do bazy danych.");
      } else {
        alert("Wystąpił błąd podczas zapisywania danych.");
      }
    } catch (error) {
      console.error("Błąd podczas zapisywania danych:", error);
    }

    setIsSaving(false);
  };

  return (
    <div>
      <button onClick={handleSave} disabled={isSaving}>
        {isSaving ? "Zapisywanie..." : "Save"}
      </button>
      <Columns columns={columns} setColumns={setColumns} />
    </div>
  );
};

export default Home;
