import React, { useState } from "react";
import "./Columns.css";
import AddItem from "./AddItem";

function Columns() {
  const [columns, setColumns] = useState([
    { title: "Column 1", items: [] },
    { title: "Column 2", items: [] },
    { title: "Column 3", items: [] },
    { title: "Column 4", items: [] },
  ]);

  const handleAddItem = (content, columnIndex) => {
    setColumns((prevColumns) => {
      const updatedColumns = [...prevColumns];
      updatedColumns[columnIndex].items.push(content);
      return updatedColumns;
    });
  };

  return (
    <div className="columns-container">
      {columns.map((column, columnIndex) => (
        <div key={columnIndex} className="column">
          <h2>{column.title}</h2>
          <button onClick={() => handleShowModal(columnIndex)}>Add Item</button>
          <ul>
            {column.items.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </div>
      ))}
      <div id="modal-root"></div>
    </div>
  );
}

export default Columns;
