import React from "react";
import DragDropContext from "react-beautiful-dnd";
import { Droppable } from "react-beautiful-dnd";
import { Draggable } from "react-beautiful-dnd";
import "./Columns.css";
import Tile from "../Tile/Tile";

const initialTiles = [
  { id: "tile-1", content: "Tile 1" },
  { id: "tile-2", content: "Tile 2" },
  { id: "tile-3", content: "Tile 3" },
  { id: "tile-4", content: "Tile 4" },
];

function Columns() {
  const [tiles, setTiles] = React.useState(initialTiles);

  const onDragEnd = (result) => {
    if (!result.destination) return;

    const newTiles = [...tiles];
    const [draggedTile] = newTiles.splice(result.source.index, 1);
    newTiles.splice(result.destination.index, 0, draggedTile);

    setTiles(newTiles);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <Droppable droppableId="columns" direction="horizontal">
        {(provided) => (
          <div
            className="columns-container"
            {...provided.droppableProps}
            ref={provided.innerRef}
          >
            {tiles.map((tile, index) => (
              <Draggable key={tile.id} draggableId={tile.id} index={index}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                  >
                    <Tile content={tile.content} index={index} />
                  </div>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </DragDropContext>
  );
}

export default Columns;
